import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import MusicList from "../components/MusicList";
import "../styles/LibraryPage.css";

const LibraryPage = () => {
  const [libraryTracks, setLibraryTracks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // ✅ redirect if not authenticated
      return;
    }

    const fetchLibrary = async () => {
      try {
        const response = await api.get("/library"); // hits /api/library due to baseURL
        setLibraryTracks(response.data);
      } catch (error) {
        console.error("Error fetching library:", error);
        // Optional: redirect to login if token expired or invalid
        if (error.response && error.response.status === 401) {
          navigate("/login");
        }
      }
    };

    fetchLibrary();
  }, [navigate]);

  const filteredTracks = libraryTracks.filter((track) =>
    track.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="library-page">
      <h1>My Library</h1>
      <input
        type="text"
        placeholder="Search tracks..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-bar"
      />
      <MusicList musicTracks={filteredTracks} onSelectTrack={() => {}} />
    </div>
  );
};

export default LibraryPage;
