import React, { useState, useEffect, useRef } from "react";
import api from "../services/api";
import MusicList from "../components/MusicList";
import MusicPlayer from "../components/MusicPlayer";
import "../styles/UserPage.css"; // Optional: keep for any custom CSS

const UserPage = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const fetchTracks = async () => {
      try {
        const res = await api.get("/user/music");
        setTracks(res.data);
      } catch (error) {
        console.error("Error loading tracks:", error);
      }
    };
    fetchTracks();
  }, []);

  const handleTrackEnd = () => {
    const currentIndex = tracks.findIndex((t) => t.id === currentTrack.id);
    const nextTrack = tracks[currentIndex + 1];
    if (nextTrack) setCurrentTrack(nextTrack);
    else setCurrentTrack(null);
  };

  const handlePlayPause = () => {
    if (audioRef.current) {
      if (isPlaying) audioRef.current.pause();
      else audioRef.current.play();
      setIsPlaying(!isPlaying);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 p-6">
      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-10">
        <div className="bg-white shadow rounded p-6">
          <h1 className="text-3xl font-bold text-blue-700 mb-4 flex items-center">
            <img src="/music-note.svg" alt="icon" className="w-6 h-6 mr-2" />
            Music Library
          </h1>
          <MusicList musicTracks={tracks} onSelectTrack={setCurrentTrack} />
        </div>

        <div className="bg-white shadow rounded p-6 flex flex-col justify-between">
          <h2 className="text-2xl font-semibold text-gray-700 mb-3">
            Now Playing:{" "}
            <span className="text-purple-600">
              {currentTrack ? currentTrack.title : "Nothing selected"}
            </span>
          </h2>
          {currentTrack && (
            <div className="space-y-4">
              <p className="text-gray-600">Artist: {currentTrack.artist}</p>
              <div className="w-full bg-gray-200 h-2 rounded-full overflow-hidden">
                <div className="h-full bg-purple-400 w-1/2 transition-all duration-300" />
              </div>
              <button
                onClick={handlePlayPause}
                className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded"
              >
                {isPlaying ? "Pause" : "Play"}
              </button>
            </div>
          )}
          <MusicPlayer
            currentTrack={currentTrack}
            onTrackEnd={handleTrackEnd}
            audioRef={audioRef}
            setIsPlaying={setIsPlaying}
          />
        </div>
      </div>
    </div>
  );
};

export default UserPage;
