import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import AdminLogin from "../components/AdminLogin";
import api from "../services/api";
import "../styles/AdminPage.css";

const AdminPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [musicTracks, setMusicTracks] = useState([]);
  const [selectedTrackId, setSelectedTrackId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const [newMusic, setNewMusic] = useState({ title: "", artist: "", s3_url: "" });
  const [editMusic, setEditMusic] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsAuthenticated(true);
      fetchMusicTracks();
    }
  }, []);

  const fetchMusicTracks = async () => {
    try {
      const response = await api.get("/admin/music");
      setMusicTracks(response.data);
    } catch (error) {
      console.error("Error fetching music:", error);
    }
  };

  const handleAddMusic = async () => {
    try {
      const response = await api.post("/admin/music", newMusic);
      setNewMusic({ title: "", artist: "", s3_url: "" });
      fetchMusicTracks();
    } catch (error) {
      console.error("Error adding music:", error);
    }
  };

  const handleUpdateMusic = async () => {
    try {
      await api.put(`/admin/music/${editMusic.id}`, editMusic);
      setEditMusic(null);
      fetchMusicTracks();
    } catch (error) {
      console.error("Error updating music:", error);
    }
  };

  const handleDeleteMusic = async (musicId) => {
    try {
      await api.delete(`/admin/music/${musicId}`);
      fetchMusicTracks();
      setShowDeleteModal(false);
    } catch (error) {
      console.error("Error deleting music:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    navigate("/admin");
  };

  return (
    <div className="admin-page">
      <header className="admin-header">
        <div className="admin-logo">üéß Admin Dashboard</div>
        <nav>
          <Link to="/">üè† Home</Link>
          <button className="logout-btn" onClick={handleLogout}>
            üö™ Logout
          </button>
        </nav>
      </header>

      {!isAuthenticated ? (
        <AdminLogin
          onLogin={() => {
            setIsAuthenticated(true);
            fetchMusicTracks();
          }}
        />
      ) : (
        <div className="admin-content">
          <h2>üéº Music Management</h2>

          {/* CREATE MUSIC FORM */}
          <div className="music-form">
            <input
              type="text"
              placeholder="Title"
              value={newMusic.title}
              onChange={(e) => setNewMusic({ ...newMusic, title: e.target.value })}
            />
            <input
              type="text"
              placeholder="Artist"
              value={newMusic.artist}
              onChange={(e) => setNewMusic({ ...newMusic, artist: e.target.value })}
            />
            <input
              type="text"
              placeholder="S3 URL"
              value={newMusic.s3_url}
              onChange={(e) => setNewMusic({ ...newMusic, s3_url: e.target.value })}
            />
            <button className="add-btn" onClick={handleAddMusic}>‚ûï Add Music</button>
          </div>

          {/* UPDATE MUSIC FORM */}
          {editMusic && (
            <div className="music-form update">
              <input
                type="text"
                value={editMusic.title}
                onChange={(e) => setEditMusic({ ...editMusic, title: e.target.value })}
              />
              <input
                type="text"
                value={editMusic.artist}
                onChange={(e) => setEditMusic({ ...editMusic, artist: e.target.value })}
              />
              <input
                type="text"
                value={editMusic.s3_url}
                onChange={(e) => setEditMusic({ ...editMusic, s3_url: e.target.value })}
              />
              <button className="update-btn" onClick={handleUpdateMusic}>‚úÖ Save Changes</button>
              <button onClick={() => setEditMusic(null)}>‚ùå Cancel</button>
            </div>
          )}

          {/* MUSIC LIST */}
          <div className="music-grid">
            {musicTracks.map((track) => (
              <div key={track.id} className="music-card">
                <div className="music-info">
                  <h3>{track.title}</h3>
                  <p>{track.artist}</p>
                </div>
                <div className="music-actions">
                  <button onClick={() => setEditMusic(track)}>‚úèÔ∏è Edit</button>
                  <button onClick={() => {
                    setSelectedTrackId(track.id);
                    setShowDeleteModal(true);
                  }}>üóëÔ∏è Delete</button>
                </div>
              </div>
            ))}
          </div>

          {/* TODO: USER MANAGEMENT SECTION */}
          <div className="user-management">
            <h2>üë• User Management (Coming Soon)</h2>
            <p>Manage users: view, delete, or update user roles.</p>
            {/* You can add fetchUsers, deleteUser, and editUser logic here */}
          </div>
        </div>
      )}

      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Are you sure you want to delete this track?</p>
            <div className="modal-buttons">
              <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
              <button onClick={() => handleDeleteMusic(selectedTrackId)}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
