import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import AdminLogin from "../components/AdminLogin";
import api from "../services/api";
import "../styles/AdminPage.css";

const AdminPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [musicTracks, setMusicTracks] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedTrackId, setSelectedTrackId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsAuthenticated(true);
      fetchMusicTracks();
    }
  }, []);

  const fetchMusicTracks = async () => {
    try {
      const response = await api.get("/admin/music");
      setMusicTracks(response.data);
    } catch (error) {
      console.error("Error fetching music:", error);
    }
  };

  const handleDeleteMusic = async (musicId) => {
    try {
      await api.delete(`/admin/music/${musicId}`);
      fetchMusicTracks();
      setShowDeleteModal(false);
    } catch (error) {
      console.error("Error deleting music:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    navigate("/admin");
  };

  return (
    <div className="admin-page">
      <header className="admin-header">
        <div className="admin-logo">🎧 Admin Dashboard</div>
        <nav>
          <Link to="/">🏠 Home</Link>
          <button className="logout-btn" onClick={handleLogout}>
            🚪 Logout
          </button>
        </nav>
      </header>

      {!isAuthenticated ? (
        <AdminLogin
          onLogin={() => {
            setIsAuthenticated(true);
            fetchMusicTracks();
          }}
        />
      ) : (
        <div className="admin-content">
          <h2>🎼 Music Management</h2>
          {musicTracks.length === 0 ? (
            <p className="empty-message">No music tracks available. Upload some soon!</p>
          ) : (
            <div className="music-grid">
              {musicTracks.map((track) => (
                <div key={track.id} className="music-card">
                  <div className="music-info">
                    <h3>{track.title}</h3>
                    <p>{track.artist}</p>
                  </div>
                  <button
                    className="delete-btn"
                    onClick={() => {
                      setSelectedTrackId(track.id);
                      setShowDeleteModal(true);
                    }}
                  >
                    🗑️ Delete
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>Are you sure you want to delete this track?</p>
            <div className="modal-buttons">
              <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
              <button onClick={() => handleDeleteMusic(selectedTrackId)}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
