import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AdminLogin from "../components/AdminLogin";

const AdminPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [musicTracks, setMusicTracks] = useState([]);
  const navigate = useNavigate();

  // Check if the admin is authenticated
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsAuthenticated(true);
      fetchMusicTracks();
    }
  }, []);

  // Fetch music tracks from the backend
  const fetchMusicTracks = async () => {
    try {
      const response = await axios.get("/api/admin/music", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setMusicTracks(response.data);
    } catch (error) {
      console.error("Error fetching music:", error);
    }
  };

  // Handle music deletion
  const handleDeleteMusic = async (musicId) => {
    try {
      await axios.delete(`/api/admin/music/${musicId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      fetchMusicTracks(); // Refresh the list
    } catch (error) {
      console.error("Error deleting music:", error);
    }
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    navigate("/admin");
  };

  return (
    <div className="admin-page">
      <h1>Admin Portal</h1>
      {!isAuthenticated ? (
        <AdminLogin onLogin={() => setIsAuthenticated(true)} />
      ) : (
        <div>
          <button onClick={handleLogout}>Logout</button>
          <h2>Music Management</h2>
          <ul>
            {musicTracks.map((track) => (
              <li key={track.id}>
                {track.title} - {track.artist}
                <button onClick={() => handleDeleteMusic(track.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
