import React, { useState, useEffect, useRef } from "react";
import api from "../services/api";
import MusicList from "../components/MusicList";
import MusicPlayer from "../components/MusicPlayer";
import "../styles/UserPage.css"; // Add custom styles

const UserPage = () => {
  const [tracks, setTracks] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const fetchTracks = async () => {
      try {
        const res = await api.get("/user/music");
        setTracks(res.data);
      } catch (error) {
        console.error("Error loading tracks:", error);
      }
    };
    fetchTracks();
  }, []);

  const handleTrackEnd = () => {
    const currentIndex = tracks.findIndex((t) => t.id === currentTrack.id);
    const nextTrack = tracks[currentIndex + 1];
    if (nextTrack) setCurrentTrack(nextTrack);
    else setCurrentTrack(null);
  };

  const handlePlayPause = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  return (
    <div className="user-page">
      <div className="library-section">
        <h1 className="library-title">Music Library</h1>
        <MusicList musicTracks={tracks} onSelectTrack={setCurrentTrack} />
      </div>
      <div className="player-section">
        <h2 className="now-playing-title">
          Now Playing: {currentTrack ? currentTrack.title : "Nothing selected"}
        </h2>
        {currentTrack && (
          <div className="track-info">
            <p className="track-artist">{currentTrack.artist}</p>
            <div className="progress-bar">
              <div className="progress" style={{ width: "50%" }}></div>
            </div>
            <div className="controls">
              <button onClick={handlePlayPause}>
                {isPlaying ? "Pause" : "Play"}
              </button>
            </div>
          </div>
        )}
        <MusicPlayer
          currentTrack={currentTrack}
          onTrackEnd={handleTrackEnd}
          audioRef={audioRef}
          setIsPlaying={setIsPlaying}
        />
      </div>
    </div>
  );
};

export default UserPage;
