import React, { useState, useEffect } from "react";
import axios from "axios";
import MusicPlayer from "../components/MusicPlayer";
import MusicList from "../components/MusicList";

const HomePage = () => {
  const [musicTracks, setMusicTracks] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);

  // Fetch music tracks from the backend
  useEffect(() => {
    const fetchMusic = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/music"); // Use the correct backend URL
        setMusicTracks(response.data);
      } catch (error) {
        console.error("Error fetching music:", error);
      }
    };
    fetchMusic();
  }, []);

  // Handle track end
  const handleTrackEnd = () => {
    const currentIndex = musicTracks.findIndex((track) => track.id === currentTrack.id);
    const nextTrack = musicTracks[(currentIndex + 1) % musicTracks.length];
    setCurrentTrack(nextTrack);
  };

  return (
    <div className="home-page">
      <h1>Welcome to Cloud Music Player</h1>
      <MusicPlayer currentTrack={currentTrack} onTrackEnd={handleTrackEnd} />
      <MusicList musicTracks={musicTracks} onSelectTrack={setCurrentTrack} />
    </div>
  );
};

export default HomePage;
